class Solution {
public:
    vector<string> generateParenthesis(int n) {
       //vector to store all possible combinations
        vector<string> res;
        //string to store current combination
        string s;
        //recursive function to generate all combinations
        dfs(n,n,s,res);
        return res;  
    }
    void dfs(int left,int right,string s,vector<string> &res){
        //base case, when there are no more left or right parentheses to place
        if(left==0 && right==0){
            //add current combination to result vector
            res.push_back(s);
            return;
        }
        //if there are still left parentheses to place, add one to the current combination
        if(left>0) dfs(left-1,right,s+'(',res);
        //if there are more right parentheses to place than left, add one to the current combination
        if(right>left) dfs(left,right-1,s+')',res);
    }
};

