                swap(s,lptr,lptr+1);
                res++;
            }
            else{
                while(r!=rptr){
                    swap(s,r,r+1);
                    r++;
                    res++;
                }
            }
        }
        return res;
    }

    public void swap(char[] s,int l,int r){
        char temp=s[l];
        s[l]=s[r];
        s[r]=temp;
    }
