int minMovesToMakePalindrome(string str) {

        int n = str.size();

        int low = 0;

        int high = n - 1;

        int count = 0;

        while(low <= high)
        {
            if(str[low] == str[high])
            {
                low++;

                high--;
            }
            else
            {
                int idx = high - 1;

                while(idx > low)
                {
                    if(str[low] == str[idx])
                    {
                        break;
                    }

                    idx--;
                }

                if(idx == low)
                {
                    count++;

                    swap(str[idx], str[idx + 1]);
                }

                else
                {
                    while(idx < high)
                    {
                        count++;

                        swap(str[idx], str[idx + 1]);

                        idx++;
                    }
                }
            }
        }

        return count;
    }
