```
class Solution {
public:
    int minMovesToMakePalindrome(string s) {

        int n=s.length();
        int left=0,right=n-1;
        int res=0;

        while(left<right)
        {
            int l=left,r=right; 
            while(s[l]!=s[r])
            l++;//traversing from the left in search of the same ele as s[r]

            //if we do not get another occurence of s[r] i.e., there is only one instance and hence it will be the middle ele
            //we swap s[l] with the previous char to check whether another instance of that char is present so that we can 
            //swap to make a valid palindromic string

        if(l==r)
        {
            //on doing this repeatedly for each window we have the single char at s[l] and finally at the last
            //iteration l will point to the middle of the string and hence the single ele will be placed at the middle of the
            //string resulting into a valid palindromic string

            swap(s[l-1],s[l]);
            res++;
            //here we do not decrease the size of the current window 
            //for checking once again in the same window
            continue;
        }
        else
        {
            while(l>left)
            {
                swap(s[l-1],s[l]);
                res++;
                l--;
            }
        }
            //swap is done and hence we are sure that s[left]==s[right]
            //now we decrease the size of the window from both the sides and do the same process again
            left++;
            right--;
        }

        return res;
    }
};
