        int currentIdx;
        boolean [] visited = new boolean [s.length()];

        int mid = s.length() / 2;

        for (int r = s.length() - 1; r > l; --r){
            if (visited[r]) continue;

            c = s.charAt(r);

            lP = charList[c - 'a'].get(charPointers[c - 'a'][0]++);
            rP = charList[c - 'a'].get(charPointers[c - 'a'][1]--);

            visited[lP] = true;
            visited[rP] = true;

