class Solution {
public:

    int minMovesToMakePalindrome(string s) {

        int low = 0;
        int high = s.length()-1;

        int count = 0;
        while (low<=high)
        {
            if (s[low] != s[high])
            {
                int i = high-1;
                while (i>=low+1)
                {
                    if (s[low] == s[i])
                    {
                        break;
                    }
                    i--;
                }

                int j = low+1;
                while (j<=high-1)
                {
                    if (s[high]==s[j])
                    {
                        break;
                    }
                    j++;
                }

                int d1 = high-i;
                int d2 = j-low;

                if (d1<=d2)
                {

                    while (i<=high-1)
                    {
                        swap(s[i],s[i+1]);
                        count++;
                        i++;
                    }
                }
                else
                {
                    while (j>=low+1)
                    {
                        swap(s[j],s[j-1]);
                        count++;
                        j--;
                    }
                }
            }
            low++;
            high--;
        }

        return count;
    }
};
