class Solution {
public:
    int minMovesToMakePalindrome(string s) {


        //here we will be using an iterative recursive approach 
        int lt=0;
        int rt=s.size()-1;
        int ans=0;
        //ans will keep track of the minimum number of swaps required to make it pallindrome in the most 
        //optimal ways
        int l;
        int r;
        while(lt<rt){
          l=lt;
          r=rt;

          while(s[l]!=s[r])l++;
          //finding the first occurence from the right side which can make the current 2 characters equal
          if(l==r){
            //then it will be part of the odd length part in btw the whole pallindrome
            swap(s[l],s[l-1]);//here we will be using the internal c++  swapping function
            ans++;
            continue;
          }
          while(l>lt){
            swap(s[l],s[l-1]);
            l--;
            ans++;
          }
          lt++;
          rt--;

        }
        return ans;
    }
};
