function minMovesToMakePalindrome(s: string): number {
    let leftIndex = 0
    let rightIndex = s.length - 1
    const sArr: string[] = Array.from(s)
    let sumChange = 0

    while(leftIndex < rightIndex){
        if(sArr[leftIndex] === sArr[rightIndex]){
            leftIndex ++
            rightIndex --
            continue
        }
        let matchRight = leftIndex + 1
        let matchLeft = rightIndex - 1

        while(sArr[matchRight] !== sArr[rightIndex]){
            matchRight++
        }
        while(sArr[matchLeft] !== sArr[leftIndex]){
            matchLeft--
        }
        if(matchRight - leftIndex <= rightIndex - matchLeft){
            sumChange += matchRight - leftIndex
            sArr.splice(matchRight, 1)
            sArr.splice(leftIndex, 0, s[rightIndex])
        }else{
            sumChange += rightIndex - matchLeft
            sArr.splice(rightIndex + 1, 0, s[leftIndex])
            sArr.splice(matchLeft, 1)
        }
        leftIndex ++
        rightIndex --
    }
    return sumChange
};
```

