        int b = 0, e = chars.Count - 1;
        while (b < e) {
            if (chars[b] != chars[e]) {
                int maxIx = -1;
                for (int i = e - 1; i > b; i--) {
                    if (chars[i] == chars[b]) {
                        maxIx = i;
                        break;
                    }
                }
                // check if this is a single letter that needs to be in the middle, 
                // the way the maths works for the middle letter is that we are leaving it
                // to swap it in the end - in order to reduce unneccessary swaps
                // and in the end when everything is in place, the cost to swap this letter
                // and put in in the middle will be the cost to put in in the middle right now
                if (maxIx == -1) {
                    sum += (e - b) / 2;
                    chars.RemoveAt (b);
                } 
                // the cost to swap the rightmost letter that's the same with the current left
                // letter will be the endIndex - currentIndex
                else {
                    sum += e - maxIx;
                    chars.RemoveAt (maxIx);
                    chars.RemoveAt (b);
                }
            } else {
                chars.RemoveAt (e);
                chars.RemoveAt (b);
            }
            e = chars.Count - 1;
        }
        return sum;
    }
}
```

