			if(s[i] == s[j]){
				i++;
				j--;
				continue;
			}
			else{
				int idx1 = -1;
				int idx2 = -1;
				for(int k=i+1; k<j; k++){
					if(s[k] == s[j]) {
						idx1 = k;
						break;
					}
				}
				for(int k=j-1; k>i; k--){
					if(s[k] == s[i]){
						idx2 = k;
						break;
					}
