    def minMovesToMakePalindrome(self, s: str) -> int:
        s = list(s)
        n = len(s)
        left, right = 0, n-1
        res = 0
        while left < right:
            if s[left] != s[right]:
                lidx, ridx = left+1, right-1
                rdelta = 1
                while s[left] != s[ridx]:
                    ridx -= 1
                    rdelta += 1
                ldelta = 1
                while s[right] != s[lidx]:
                    lidx += 1
                    ldelta += 1
                if ldelta < rdelta:
                    for i in range(lidx,left,-1):
                        s[i-1],s[i] = s[i],s[i-1]
                    res += ldelta
                else:
                    for i in range(ridx,right):
                        s[i],s[i+1] = s[i+1],s[i]
                    res += rdelta
            left += 1
            right -= 1
        return res
