class Solution {
    fun minMovesToMakePalindrome(str: String): Int {
        var ans = 0
        var s = StringBuilder(str)

        while (s.length > 1) {
            if (s[0] == s[s.length-1]) {
                s = s.deleteCharAt(0)
                s = s.deleteCharAt(s.length-1)
                continue
            }
            for (i in s.length-1 downTo 0) {
