    int minMovesToMakePalindrome(string s) {
        int left = 0, right = s.length()-1, center = -1, swaps = 0;
        while (left<right) {
            if (s[left] == s[right]) {
                left++;
                right--;
                continue;
            }
            int index=-1;
            for (int i=left+1;i<right;i++) {
                if (s[i] == s[right]) {
                    index=i;
                    break;
                }
            }
            if (index == -1) {
                center = right;
                right--;
                continue;
            }
            for (int i=index-1;i>=left;i--) {
                swap(s[i], s[i+1]);
                swaps++;
            } 
            left++;
            right--;
        }
        if (center!=-1) {
            swaps+=center-s.length()/2;
        }
        return swaps;
    }
};
