class Solution:
    def minMovesToMakePalindrome(self, s: str) -> int:
        ans = 0
        while len(s) > 2:
            if s[0] != s[-1]:
                l, r = s.find(s[-1]), s.rfind(s[0])
                if l < len(s) - r - 1: 
                    ans += l
                    s = s[:l] + s[l+1:-1]
                else:
                    ans += len(s) - r - 1
                    s = s[1:r] + s[r+1:]
            else:
                s = s[1:-1]
        return ans
