int solve( string &s , int i , int j )
    {
        if( i >= j ) return 0 ;

        if( s[i] == s[j] )
        {
            return solve( s , i+1 , j-1 ) ;
        }

        int ans = INT_MAX ;

        int p1 = i + 1 ;
        int p2 = j - 1 ;


        while( p1 < j  && s[p1] != s[j] )
        {
            p1++ ;
        }

        while( p2 > i  && s[p2] != s[i] )
        {
            p2-- ;
        }

        int temp = 0 ;

        if( p1 < j && p2 > i )
        {
            if( (p1 - i ) <= ( j - p2 ) )
            {
                temp = 1 ;
            }
            else
            {
                temp = 2 ;
            }
        }
        else if( p1 < j )
        {
            temp = 1 ;

        }
        else if( p2 > i )
        {
            temp = 2 ;
        }

        if(temp == 1 )
        {
            ans = p1 - i ;
            while( p1 > i )
            {
                swap( s[p1] , s[p1-1] ) ;
                p1-- ;
            }

        }
        else if( temp == 2 )
        {
            ans = j - p2 ;
            while( p2 < j )
            {
                swap( s[p2] , s[p2 + 1] ) ;
                p2++ ;
            }

        }

        return ans + solve( s , i+1 , j-1 ) ;

