                        closestIdx = min(closestIdx, i)
                return (closestIdx-left, closestIdx)

            else:
                closestIdx = left
                swapWith = s[left]
                for i in range(left+1, right):
                    if s[i] == swapWith:
                        closestIdx = max(i, closestIdx)
                return (right - closestIdx, closestIdx)

        while first < last:
            if s[first] == s[last]:
                first += 1
                last -= 1
                continue
            leftSwap, leftIdx = swapsRequired(first, last, 1)
            rightSwap, rightIdx = swapsRequired(first, last, 0)
            if leftSwap < rightSwap:
                while first < leftIdx:
                    s[leftIdx], s[leftIdx-1] = s[leftIdx-1], s[leftIdx]
                    leftIdx -= 1
            else:
                while rightIdx < last:
                    s[rightIdx], s[rightIdx+1] = s[rightIdx+1], s[rightIdx]
                    rightIdx += 1
            totalSwaps += min(leftSwap, rightSwap)
            first += 1
