class Solution {
    public int minMovesToMakePalindrome(String s) {
        //using two pointer approach

      int ans=0; // creating ans variable
        char arr[]=s.toCharArray(); //convert string to character array
        int i=0;  //creating i pointer
        int j=arr.length-1; // creating j pointer
        while(i<j){ 
            int high=j; //crating high variable
            if(arr[i]==arr[j]){
                i++;
                j--;
            }
            else{
                while(arr[i]!=arr[high]){
                    high--;
                }

                if(i==high) {
                 swap(arr,high,high+1);  // mid elememt
                   ans++;
                continue;
                }
                else{

                 while(high<j){  
                    swap(arr,high,high+1);
                    ans++;
                    high++;
                }
            }
            }
        }
        return ans;

    }
    // write swap function to swap 
    public static void swap(char ch[],int i,int j){

        char t=ch[i];
        ch[i]=ch[j];
        ch[j]=t;
