class BIT (var sz:Int) {
        sz+=1 // size internal Array with size+1 because BIT does not use index 0
        var arr:Array[Int]=Array.fill[Int](this.sz){0}
    def add(i:Int, v:Int) : Unit = { //note that we add the incremental change, not update !
        var idx=i+1
        while (idx<sz) {  
            arr(idx)+=v
            idx += idx & -idx
        }
    }
    def query(i:Int) ={
        var sum=0;
        var idx=i+1;
        while (idx!=0) {  
            sum+= arr(idx) 
            idx -= idx & -idx
        }
        sum
    }
};
