    def minMovesToMakePalindrome(s: String): Int = {
        var n=s.length
        if (n<=1) 0
        else if (s(0)==s.last) minMovesToMakePalindrome(s.slice(1,s.length-1))
        else {
            val (l,r)=(s.indexOf(s.last),s.lastIndexOf(s(0))) //(l,r) = (,) both parenthesis mandatory
            if (r==0) n/2+minMovesToMakePalindrome(s.substring(1))
            else if (l==s.size-1) n/2+minMovesToMakePalindrome(s.substring(0,s.size-1))
