class Solution {
    public int minMovesToMakePalindrome(String s) 
    {
        int strt = 0;
        int end = s.length()-1;
        int ans = 0;
        while(strt<end)
        {
            if(s.charAt(end) == s.charAt(strt))
            {
                strt++;
                end --;
                continue;
            }
            int r__ = end;
            while(s.charAt(r__) != s.charAt(strt))
            r__--;


            if(r__ == strt)
            {
                ans += s.length()/2 - strt;
                strt++;
            }
            else
            {
                ans += end - r__;
                while(end>r__)
