            }
            //strt++;

        }
        return ans ;
    }
    public String swap(String s , int i , int j)
    {
        StringBuilder sb = new StringBuilder(s);

        char temp = s.charAt(i);
        sb.setCharAt(i,s.charAt(j));
        sb.setCharAt(j,temp);
        return sb.toString();

    }
}
