check left and right and pick the minimum one

```
class Solution {
    public int minMovesToMakePalindrome(String s) {

        int left = 0, right = s.length() - 1;
        char[] chars = s.toCharArray();
        int count = 0;
        while(!isPalindrome(chars)){

            count += makeRight(chars, left++, right--); 
        }

        return count;

    }


    public int makeRight(char[] chars, int left, int right){

        char leftChar = chars[left], rightChar = chars[right];

        if(leftChar == rightChar) return 0;

        int moveRight = chars.length, moveLeft = chars.length;

        for(int i = right - 1; i > left; i--){

            if(chars[i] == leftChar){
                moveRight = right - i;
                break;
            }
        }

        for(int i = left + 1; i < right; i++){
            if(chars[i] == rightChar){
                moveLeft = i - left;
                break;
            }
        }

        if(moveLeft < moveRight){
            swap(chars, left, moveLeft, true);
            return moveLeft;
        }else{
            swap(chars, right, moveRight, false);
            return moveRight;
        }


    }


    public void swap(char[] chars, int start, int steps, boolean isMovedLeft){

        if(isMovedLeft){
            for(int i = start + steps; i > start; i--){
                char temp = chars[i];
                chars[i] = chars[i - 1];
                chars[i - 1] = temp;
            }
        }else{
            for(int i = start - steps; i < start; i++){
                char temp = chars[i];
                chars[i] = chars[i + 1];
                chars[i + 1] = temp;
            }
        }

    }


    public boolean isPalindrome(char[] chars){

        int left = 0, right = chars.length-1;

        while(left < right){
            if(chars[left++] != chars[right--]) return false;
        }

        return true;
    }
}
