class Solution {
public:
    int minMovesToMakePalindrome(string s) {
        int n=s.size();
        int left=0;
        int right=n-1;
        int count=0;
        int mid=n/2;
        for(int i=0;i<n/2;i++)
        {
            char a=s[left];
            char b=s[right];
//finding the closest duplicate and swapping.
            if(a!=b)
            {
                int count1=0;
                for(int j=left+1;j<right;j++)
                {
                    if(s[j]!=b)
                    {
                        count1+=1;
                    }
                    else break;
                }
                int count2=0;
                for(int j=right-1;j>left;j--)
                {
                    if(s[j]!=a)
                    {
                        count2+=1;
                    }
                    else break;
                }
                if(count1>count2) //means that duplicate of a is
                //closer to right index of our window.
                {
                    //now we will swap that duplicate till it reaches 
                    //till it reaches the right'th index.
                    int k=right-count2-1;
//k stores the index of that duplicate no.
                    count+=count2+1;
                    while(k<right)
                    {
                        swap(s[k],s[k+1]);
                        k++;
                    }
                }
                else 
                {
//else either both have same distance or b is closer to left index,
//in which case, we will swap it, till it reaches left'th index
                    int k=count1+1+left;
//k stores the index of that duplicate no.
                    count+=count1+1;
                    while(k>left)
                    {
                        swap(s[k],s[k-1]);
                        k--;
                    }
                }

            }
//decreasing the size of the subarray.
            left+=1;
            right-=1;
        }
        return count;
    }
};
