      // If no match is found till s[l], we know that this is the mid value for an odd lengthed palindrome
      // Since mid remains unique in odd lengthed palindrome, we store its index for future,
      // skip working on s[l] and continue to the next iteration (here we're also avoiding making any change to r)
      if (i === l) {
        midMisplacedIdx = l;
        l++;
        continue;
      }
    }

    l++;
    r--;
  }

  // At the end, we move s[midMisplacedIdx] to mid and compute the swap count as (mid - l)
  // We don't have to swap here as we're only interested in the count
  if (midMisplacedIdx >= 0 && midMisplacedIdx < mid) {
    bubbleUp(s, midMisplacedIdx, mid); // can be skipped
    res += (mid - midMisplacedIdx);
  }

  return res;
};

// Better call it sifting or bubbling to the right but the question calls this swap
// We're basically bubbling an item from one index to the other towards its right
function bubbleUp(arr, from, to) {
  for (let i = from; i < to; i++) {
    [arr[i],arr[i + 1]] = [arr[i + 1],arr[i]];
