      // Here we need swapping as both the chars are not equal
      // We start from the right at position (r - 1) till (l + 1) to find a match for s[l]
	  // We need the value of i after the loop ends, so not assigning it within the loop initiation
      let i = r - 1;
      for (; i > l; i--) {
        if (s[l] === s[i]) {
          bubbleUp(s, i, r);
          res += (r - i);
          break;
