class Solution {
    public int minMovesToMakePalindrome(String s) {
        char[] arr = s.toCharArray();
        int count = 0, rightLimit = arr.length - 1, oddIndex = -1;
        // simulation: for avery element in the string find the nearest right-most compliment 
        // and make the extremeties a palindrome like : b[a]......[a]...b -> ba.........ab
        // continue inwards until you reach the centre of the string
        for (int left = 0; left < arr.length / 2; left++) {
            // assume that the current character is single occuring character
            oddIndex = (oddIndex == -1 ? left : oddIndex); 
            for (int right = rightLimit; right > left; right--) {
                if (arr[right] == arr[left]) {
                    // refute assumption that the current character is single occuring character
                    oddIndex = (oddIndex == left ? -1 : oddIndex);
                    // swap current right counterpart to make extremes palindrome
                    // count the number of swaps required in the process and add to answer
                    count += swapChars(arr, right, rightLimit);
                    // reduce the right limit, this implies (rightLimit - endOfString] is now a palindrome
                    rightLimit--;
                    break;
                }
            }
        }

        if (oddIndex != -1) {
            // if the oddIndex has a non-negative value
            // swap it to the centre of the string and account for those swaps
            count += swapChars(arr, oddIndex, arr.length / 2);
        }
        return count;
    }

    // simulate swapping, move 1 character each to the left in the given range
    // and then replace the final character with the first character
    int swapChars(char[] arr, int left, int right) {
        char temp = arr[left];
        for (int i = left; i < right; i++) arr[i] = arr[i + 1];
        arr[right] = temp;
        return right - left;
    }
}
