class Solution {
public:
    int minMovesToMakePalindrome(string s) {
        if(s.length() < 3) return 0;
		// here we are thinking to finding every character that is nearest and then shifting it to that position to make palindrome 
        int pt1 = 0;
        int pt2 = s.length()-1;
        int ans = 0;
        int tpl = 0 , tpr = pt2; // to store temporary pointer left and right ...
        while(pt1 < pt2){
            // if both end have not same character then come inside and make it same 
            if(s[pt1] != s[pt2]){
                // search in left for right end character and in right leftmost character
                tpl = pt1 + 1;
                tpr = pt2 - 1;
                // bool to store which pointer found the character in first
                bool l = 0 , r = 0;
                while(tpl < pt2 && tpr > pt1){
                    if(s[tpl] == s[pt2]){
                        l = 1;
                        break;
                    }
                    if(s[tpr] == s[pt1]){
                        r = 1;
                        break;
                    }
                    tpl++;
                    tpr--;
                }
                // found
                if(l){
                    // Here if left pointer found character so we have to shift characters to right
                    int t = tpl;
                    // shifting characters
                    while(t > pt1){ s[t] = s[t-1]; ans++; t--;}
                    // finally changing pt1 character to pt2 as this character if found
                    s[pt1] = s[pt2];
                }else{
                    // Here if right pointer found character so we have to shift characters to left
                    int t = tpr;
                    while(t < pt2){ s[t] = s[t+1]; ans++; t++;}
                    s[pt2] = s[pt1];
                }
            }
            pt1++;
            pt2--;
        }
        return ans;
    }
};
