class Solution {
    public int find(char[] sArr, char key, int from, int to){
        for(int i=from;i<=to; i++){
            if(key == sArr[i])
                return i;
        }
        return -1;
    }
    public void swap(char[] sArr, int i, int j){
        char t = sArr[i];
        sArr[i] = sArr[j];
        sArr[j] = t;
    }
    public void shift(char[] sArr, int from, int to){
        for(int i=from;i>to;i--){
            swap(sArr, i, i-1);
        }
    }
    public int minMovesToMakePalindrome(String s) {
        char[] sArr = s.toCharArray();
        int n = sArr.length;
        int left = 0;
        int right = sArr.length-1;
        int ans = 0;
        while(left<right){
            int index = find(sArr, sArr[right], left, right);
            if(index == right){
                ans += right-n/2;
                right--;
            }
            else{
                shift(sArr, index, left);
                ans += index-left;
                left++;
                right--;
            }
        }
        return ans;
    }
}
