class Solution {
public:
    int minMovesToMakePalindrome(string s) {
        int result = 0;
        int left = 0, right = s.length()-1;
        while(left < right) {
            int l = left, r = right;
            while(s[l] != s[r])
                r--;
            // if the element we reached is the middle element then take the element to the adjacent index
            if(l == r) {
                swap(s[r], s[r+1]);
                result++;
                continue;
            } 
            // if the element is a normal element then set it to its right position
            else {
                while(r < right) {
                    swap(s[r], s[r+1]);
                    r++;
                    result++;
                }
            }
            left++;
            right--;
        }
        return result;
    }
};
