/* Time Limit is Exceeded for some case. Plase see the below code to understand the logic only. */
public class Solution {
    public int MinMovesToMakePalindrome(string s) {
        int ans = 0;
        // 2 pointers -- left (start), right (end)
        int left = 0;
        int right = s.Length - 1;

        // l and r copies to initial pointers because these will be moved
        int l, r = 0;
        while(left < right)
        {
            l = left;
            r = right;

            // move r towards l till matching char is found
            while(s[l] != s[r])
                r--;

            if(l == r) // there is only 1 character in string so it should be in center
            {
                s = Swap(r, (r + 1), s);
                ans++;
                continue;
            }
            else // there are 2 (or more) same chars and this other char should be moved to opposite end
            {
                while(r < right) // move second char till r = right
                {
                    s = Swap(r, (r + 1), s);
                    ans++;
                    r++;
                }
            }
            // after each iteration move left and right towards each other
            left++;
            right--;
        }

        return ans;
    }

    private static string Swap(int index1, int index2, string str)
    {
        char[] strChar = str.ToCharArray();
        char temp = strChar[index1];
        strChar[index1] = strChar[index2];
        strChar[index2] = temp;

        return new string(strChar);
    }
}
