class Solution {
    public int minMovesToMakePalindrome(String s) {
        int length = s.length();
        if (length == 0 || length == 1) return 0;

        char[] str = s.toCharArray();
        int start = 0, end = length - 1;
        int count = 0;
        while (start < end) {
            if (str[start] != str[end]){
                boolean isSwapped = false;
                for (int i = start + 1; i < end; i++){
                    if (str[start] == str[i]){
                        char temp = str[i];
                        str[i] = str[end];
                        str[end] = temp;
                        count++;
                        isSwapped = true;
                        break;
                    }else if (str[end] == str[i]){
                        char temp = str[i];
                        str[i] = str[start];
                        str[start] = temp;
                        count++;
                        isSwapped = true;
                        break;
                    }
                }
                if (!isSwapped) return 0;
            }
            start++;
            end--;
        }

        return count;
    }
}
