def minMovesToMakePalindrome(self, s: str) -> int:
        n = len(s)
        idx = collections.defaultdict(collections.deque) ; 
        for i,j in enumerate(s):
            idx[j].append(i)
        p = [0] * len(s)
        j = 0 
        for i in s:
            if len(idx[i]) > 1:
                p[j] = idx[i].popleft()
                p[n-j-1] = idx[i].pop()
                j += 1
            elif len(idx[i]) == 1:
                p[n//2] = idx[i].pop()
        from sortedcontainers import SortedList
        res = 0
        A = SortedList()
        for i in p[::-1]:
            res += A.bisect(i)
            A.add(i)
        return res
