class Solution {
    public int minMovesToMakePalindrome(String s) {

        int left = 0;
        int right = s.length() -1;
        int ans = 0;

        char[] str = s.toCharArray();

        while(left < right)
        {
            // System.out.println("-------------------------------------");
            // System.out.println(str[left] + " <----> " + str[right]);
            if(str[left] == str[right])
            {
                left++;
                right--;
                // System.out.println(left + " <--> " + right);
            }
            else
            {
                int matchIdx = -1;
                for(int goRight = left+1; goRight < right; goRight++)
                {
                    if(str[goRight] == str[right])
                    {
                        matchIdx = goRight;
                        break;
                    }
                }

                if(matchIdx != -1)
                {
                    for(int goLeft = matchIdx; goLeft > left; goLeft--)
                    {
                        str = swap(str, goLeft, goLeft-1);
                        ans++;
                        // System.out.println("swap ->  " + String.valueOf(str));
                    }
                }
                else
                {
                    str = swap(str, right, right-1);
                    ans++;
                    // System.out.println("swap else -> " + String.valueOf(str));
                }

            }


        }

        // System.out.println(String.valueOf(str));

        return ans;

    }

    char[] swap(char[] str, int i , int j)
    {
        char tmp = str[i];
        str[i] = str[j];
        str[j] = tmp;
        return str;
    }

}

