int minMovesToMakePalindromeImpl(string& s, int i, int j) {
        if (i >= j) {
            return 0;
        }
        int k = j;
        while (s[k] != s[i]) {
            --k;
        }
        if (k == i) {
            swap(s[k], s[k + 1]);
            return minMovesToMakePalindromeImpl(s, i, j) + 1;
        }
        rotate(begin(s) + k, begin(s) + k + 1, begin(s) + j + 1);
        return minMovesToMakePalindromeImpl(s, i + 1, j - 1) + j - k;
    }

    int minMovesToMakePalindrome(string s) {
        return minMovesToMakePalindromeImpl(s, 0, size(s) - 1);
    }

