class Solution {
public:
    int minMovesToMakePalindrome(string s) {
        int n = s.size();
        int i = 0;
        int j = s.size() - 1;
        int cnt = 0;
        while(i < j){
            if(s[i] != s[j]){
                int k = j;
                string str = s;
                while(s[i] != s[k] and k >= 0){
                    k--;
                }
                int c1 = 0;
                while(str[i] != str[j] and k < (n-1)){
                    swap(str[k] , str[k+1]);
                    c1++;
                    k++;
                }

                k = i;

                string t = s;
                while(s[j] != s[k] and k < (n-1)){
                    k++;
                }
                // cout<<s<<k<<endl;
                int c2 = 0;
                while(t[i] != t[j] and k > 0){
                    swap(t[k] , t[k-1]);
                    c2++;
                    k--;
                }

                // cout<<c1<<" "<<c2<<endl;
                if(c1 > c2){
                    cnt += c2;
                    s = t;
                }
                else{
                    cnt += c1;
                    s = str;
                }
            }else{
                i++;
                j--;
            }
        }
        cout<<s<<endl;

        return cnt;
    }
};
