    int minMovesToMakePalindrome(string s) {
        int n=s.length(),ans=0;
        int l=0,r=n-1;
        while(l<r)
        {
            int p,q;
            if(s[l]==s[r])
            {
                l++;
                r--;
            }
            else
            {
                int key1=s[r],key2=s[l];
                p=l+1;
                while(p<r)
                {
                    if(key1!=s[p])
                        p++;
                    else
                        break;
                }
                q=r-1;
                while(q>l)
                {
                    if(key2!=s[q])
                        q--;
                    else
                        break;
                }
                if(q!=l&&p!=r)
                {
                    ans+=min(p-l,r-q);
                    if(p-l<r-q)
