    int minMovesToMakePalindrome(string s) {
        int n=s.size();
        int i=0,j=n-1;
        int count=0;
        while(i<j){
            if(s[i]==s[j]){
                i++;
                j--;
            }
            else{
                int k=i;
                while(k<j && s[k]!=s[j]){
                    /*i'm finding the other pair of
                    the element which should be at opp position;*/
                    k++;
                }
                if(k==j){ 
                    /* element is odd and should
                    be present at the middle of the string;*/
                    swap(s[j-1],s[j]);
                    count++;
                }
                else{
                    while(k>0 && s[i]!=s[j]){ 
                        /*this loop will continue till the we've
                        not set both of the ends to be equal.*/
                        swap(s[k-1],s[k]);
                        count++;
                        k--;
                    }
                }
            }
        }
        return count;
    }
};
