class Solution {
public:
    int find(string s)
    {
        int i=0,j=s.size()-1;
        if(s.size()==1)return 0;
        if(s[i]==s[j])
        {
            s.pop_back();
             if(s.size()==1)return 0;
            return find(s.substr(1));
        }
        else
        {
            int k=j;
            while(s[k]!=s[i])k--;
            int k2=i;
            while(s[k2]!=s[j])k2++;
            if(j-k<k2-i)
            {
                int t=j-k;
                while(k<j){swap(s[k],s[k+1]);k++;}
                s.pop_back();
                return find(s.substr(1))+t;
            }
            else
            {
                int t=k2-i;
                while(k2>0){swap(s[k2],s[k2-1]);k2--;}
                s.pop_back();
                return find(s.substr(1))+t;
            }
