class Solution {
public:
    int minMovesToMakePalindrome(string a) {

        int n = a.size(),g,w,k1,k2;

        unordered_map<char,int> m;    // For maping every char of string

        for(int i=0;i<n;i++)
        {
            m[a[i]]++;
        }

        int ans =0 ,i =0 ,j= n-1;     // Two pointer one from start another from end of string

        while(1)
        {
            j = n-i-1;
            if(i>=j)                          
                return ans;                  // Return ans if pointer i greater or equal to pointer j
            if(a[i]!=a[j])                     // If both pointer character are diffrent
            {

                w = -1 ;
                g= -1;
                k1 = INT_MAX;
                k2 = INT_MAX;
                for(int k=i+1;k<j;k++)              // Find same char as a[j] and store distance in variable
                {
                    if(a[k]==a[j])
                    {
                        k1 =(k-i);
                        g = k;
                        break;
                    }
                }

                if(m[a[i]]!=1)                                 // If a[i] count is 1 means there is no more same char as a[i]
                {
                    for(int k=j-1;k>i;k--)            // Find same char as a[i] and store distance in variable
                    {
                        if(a[k]==a[i])
                        {
                            k2 = (j-k);
                            w = k;
                            break;
                        }
                    }

                }

                if(k1<k2)                              // Select smallest distance
                {
                    for(int k=g-1;k>=i;k--)
                    {
                        swap(a[k],a[k+1]);                  // Swap to tiil ith position
                    }
                    m[a[j]]-=2;                                // Reduced char from map
                    ans += k1;                               // Add to ans
                }
                else
                {
                    for(int k=w+1;k<=j;k++)                  //  Swap to tiil jth position
                    {
                        swap(a[k],a[k-1]);
                    }
                    m[a[i]]-=2;                                             // Reduced char from map
                    ans += k2;                                              // Add to ans
                }

            }
            i++;

        }

        return ans;        

    }
};
