class Solution:
    @lru_cache(None)
    def minMovesToMakePalindrome(self, s: str) -> int:
        n = len(s)
        if n<=1:
            return 0
        if s[0] == s[-1]:
            return self.minMovesToMakePalindrome(s[1:-1])
        i = 1
        while s[i]!=s[-1]:
            i+=1
        j = n - 2
        while s[j]!=s[0]:
            j-=1
        if i <= n - 1 - j:
            return i + self.minMovesToMakePalindrome(s[:i]+s[i+1:-1])
        else:
            return n - 1 - j + self.minMovesToMakePalindrome(s[1:j]+s[j+1:])
