class Solution {
public:
    int minMovesToMakePalindrome(string s) {
        const auto shift_fwd = [&s](int idx_src, int idx_dest) {
            const char val = s[idx_src];
            for (int i = idx_src; i + 1 <= idx_dest; ++i) {
                s[i] = s[i + 1];
            }
            s[idx_dest] = val;
        };        

        const auto shift_rev = [&s](int idx_src, int idx_dest) {
            const char val = s[idx_src];
            for (int i = idx_src; i - 1 >= idx_dest; --i) {
                s[i] = s[i - 1];
            }
            s[idx_dest] = val;
        };        

        const auto shift = [&](int idx_src, int idx_dest) {
            if (idx_src < idx_dest) {
                shift_fwd(idx_src, idx_dest);
            } else if (idx_src > idx_dest) {
                shift_rev(idx_src, idx_dest);
            }
        };        

        int total_ops = 0; 
        int i = 0, j = s.size() - 1;
        for (; i < j; ++i, --j) {
            if (s[i] == s[j]) {
                continue;
            }

            int lhs_ops = INT_MAX;
            int lhs_k = -1;
            {
                int k = j - 1;            
                for (; k > i; --k) {
                    if (s[k] == s[i]) {
                        break;
                    }
                }
                if (k != i) {
                    lhs_k = k;
                    lhs_ops = j - k;
                }
            }

            int rhs_ops = INT_MAX;
            int rhs_k = -1;
            {
                int k = i + 1;
                for (; k < j; ++k) {
                    if (s[k] == s[j]) {
                        break;
                    }
                }
                if (k != j) {
                    rhs_k = k;
                    rhs_ops = k - i;
                }
            }

            if (lhs_ops < rhs_ops) {
                total_ops += lhs_ops;
                shift(lhs_k, j);
            } else {
                total_ops += rhs_ops;
                shift(rhs_k, i);
            }
        }            

        return total_ops;
    }
};
```

