class Solution {
    void dijkstra(vector<vector<pair<long long, long long>>>& adj, vector<long long>& dist, long long source, long long n) {

        priority_queue<pair<long long, long long>, vector<pair<long long, long long>>, greater<pair<long long, long long>>> pq;
        pq.push({0LL, source});

        dist[source] = 0LL;

        while(pq.size()) {
            long long dis = pq.top().first;
            long long node = pq.top().second; 
            pq.pop();

            if(dis > dist[node]) continue;

            for(auto& it : adj[node]) {
                int adjnode = it.first;
                int wt = it.second; 

                if(wt + dis < dist[adjnode]) {
                    dist[adjnode] = (long long)wt + (long long)dis;
                    pq.push({dist[adjnode], adjnode});
                }
            }
        }

    }
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {

        vector<vector<pair<long long, long long>>> adj(n);
        vector<vector<pair<long long, long long>>> rev_adj(n);

        for(auto& edge : edges) {
            adj[edge[0]].push_back({edge[1], edge[2]});
            rev_adj[edge[1]].push_back({edge[0], edge[2]});
        }

        vector<long long> dist_from_src1(n, 1e18);
        vector<long long> dist_from_src2(n, 1e18);
        vector<long long> dist_from_dest(n, 1e18);

        dijkstra(adj, dist_from_src1, src1, n);
        dijkstra(adj, dist_from_src2, src2, n);
        dijkstra(rev_adj, dist_from_dest, dest, n);


        long long minWeight = 1e18;

        for(int i=0; i<n; i++) {
            if(dist_from_src1[i] == 1e18 or dist_from_src2[i] == 1e18 or dist_from_dest[i] == 1e18) continue;
            minWeight = min(minWeight, dist_from_src1[i] + dist_from_src2[i] + dist_from_dest[i]);
        }

        return minWeight >= 1e18 ? -1 : minWeight;
    }
};
