class Solution {
    public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {
        List<int[]>[] graph = new List[n], graph_rev = new List[n];
        for (int i = 0; i < n; i++){
            graph[i] = new ArrayList<>();
            graph_rev[i] = new ArrayList<>();
        }
        for (int[] e : edges) {
            graph[e[0]].add(new int[]{e[1], e[2]});
            graph_rev[e[1]].add(new int[]{e[0], e[2]});
        }
        long ans = Long.MAX_VALUE;
        long[] a = dijkstra(graph_rev, n, dest);
        long[] b = dijkstra(graph, n, src1);
        long[] c = dijkstra(graph, n, src2);

        if(b[dest]==Long.MAX_VALUE || c[dest]==Long.MAX_VALUE) return -1;

        for(int i=0; i<n ; i++){
            if(a[i]==Long.MAX_VALUE || b[i]==Long.MAX_VALUE || c[i]==Long.MAX_VALUE) continue;
            ans = Math.min(ans, a[i]+b[i]+c[i]);
        }
        return ans==Long.MAX_VALUE ? -1 : ans;
    }
    long[] dijkstra(List<int[]>[] graph, int n, int src){
        long[] dist = new long[n];
        Arrays.fill(dist, Long.MAX_VALUE);
        dist[src] = 0;
        PriorityQueue<long[]> minHeap = new PriorityQueue<>((a, b) -> Long.compare(a[0], b[0]));
        minHeap.offer(new long[]{0, src});  // dist, src
        while (!minHeap.isEmpty()) {
            long[] top = minHeap.poll();
            long d = top[0];
            int u = (int) top[1];
            if (d > dist[u]) continue;
            for (int[] nei : graph[u]) {
                int v = nei[0], time = nei[1];
                if (dist[v] > d + time) {
                    dist[v] = d + time;
                    minHeap.add(new long[]{dist[v], v});
                }
            }
        }
        return dist;
    }
}
