class Solution {
public:

    void dik(int src,vector<long long int>&dis,vector<pair<int,int>>adlist[])
    {
        set<pair<long long int,int>> st;
        st.insert(make_pair(0,src));
        dis[src]=0;        
        while(st.size()!=0)
        {
            auto p=*st.begin();
            st.erase(st.begin());

            for(auto child:adlist[p.second])
