typedef pair<long, int> node;

class Solution {
private:
    vector<long> dijkstra(int src, int n, vector<vector<node>> &graph) {
        // withoud used array gives TLE
        // bcz, a node may be pushed into queue multiple time
        // iterating all of its child again and again is not fruitful

        vector<bool> used(n, false);
        vector<long> dist(n, 1e18);
        dist[src] = 0;

        priority_queue<node, vector<node>, greater<node>> q;
        q.push({0, src});
        while(!q.empty()) {
            auto curr = q.top();
            q.pop();
            // long w = curr.first;
            long u = curr.second;

            if(used[u])
                continue;
            used[u] = true;

            for(auto it: graph[u]) {
                int v = it.first;
                long w = it.second;
                if(dist[u] + w < dist[v]) {
                    dist[v] = dist[u] + w;
                    q.push({dist[v], v});
                }
            }
        }

        return dist;
    }

public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<node>> graph(n), rev_graph(n);
        for(auto it: edges) {
            int u = it[0];
            int v = it[1];
            int w = it[2];
            graph[u].push_back({v, w});
            rev_graph[v].push_back({u, w});
        }

        vector<long> dist1 = dijkstra(src1, n, graph);
        vector<long> dist2 = dijkstra(src2, n, graph);
        vector<long> dist3 = dijkstra(dest, n, rev_graph);

        long ans = 1e18;
        for(int i = 0; i < n; i++) {
            if(dist1[i] != 1e18 && dist2[i] != 1e18 && dist3[i] != 1e18)
                ans = min(ans, dist1[i] + dist2[i] + dist3[i]);
        }

        return ans == 1e18 ? -1 : ans;
    }
};



