class Solution {
private:
    typedef pair<long, long>p;
    void Dijkstra(vector<vector<p>> &graph,int source,vector<long>&distTo)
    {
        priority_queue<p, vector<p>, greater<p>>pq;
        distTo[source] = 0;
        pq.push(make_pair(0,source)); // (dist,from)
        while(!pq.empty())
        {
            long long dist = pq.top().first;
            long long prev = pq.top().second;
            pq.pop();
            vector<p>::iterator it;
            if(distTo[prev] < dist)
                continue;
            for(auto it : graph[prev])
            {
                long next = it.first;
                long nextDist = it.second;
                if(distTo[next] > dist + nextDist)
                {
                    distTo[next] = distTo[prev] + nextDist;
                    pq.push(make_pair(distTo[next],next));
                }
            }
        }
    }
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) 
    {
        vector<vector<p>>graph(n);
        vector<vector<p>>revGraph(n);

        for(auto it : edges)
        {
            int u = it[0], v = it[1], w = it[2];
            graph[u].push_back({v,w});
            revGraph[v].push_back({u,w});
        }

        vector<long>d1(n, LONG_MAX), d2(n,LONG_MAX), d3(n,LONG_MAX);

        Dijkstra(graph,src1,d1);
        Dijkstra(graph,src2,d2);
        Dijkstra(revGraph,dest,d3);

        long res = LONG_MAX;
        for(int i = 0 ; i<n ; i++)
        {
            if(d1[i] == LONG_MAX || d2[i] == LONG_MAX || d3[i] == LONG_MAX)
                continue;
            res = min(res, d1[i] + d2[i] + d3[i]);
        }
        return res == LONG_MAX ? -1: res;
    }
};
