class Solution:
    def minimumWeight(self, n: int, edges: List[List[int]], src1: int, src2: int, dest: int) -> int:
        graph = [[] for _ in range(n)]
        trans = [[] for _ in range(n)]
        for u, v, w in edges: 
            graph[u].append((v, w))
            trans[v].append((u, w))

        def bfs(x, graph): 
            dist = [inf] * n
            dist[x] = 0 
            queue = deque([(x, 0)])
            while queue: 
                u, w = queue.popleft()
                if dist[u] == w: 
                    for v, ww in graph[u]: 
                        if w+ww < dist[v]: 
                            dist[v] = w+ww
                            queue.append((v, w+ww))
            return dist

        ds1 = bfs(src1, graph)
        ds2 = bfs(src2, graph)
        dd = bfs(dest, trans)

        ans = min(x+y+z for x, y, z in zip(ds1, ds2, dd))
        return ans if ans < inf else -1 
