    static long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {

        //1-Find shortest paths from src1 to all nodes.
        //2-Find shortest paths from src2 to all nodes.
        //3-Find shortest paths from dest to all nodes (with a reversed graph to avoid TLE while finding all direct/indirect neighbors of dest).
        //Find if there exist node such that src1 and src2 can reach to, and this node can reach dest.
        //If it exists, then your sum is minDist(src1 to node) + minDist(src2 to node) + minDist(node to destination).
        //Find minimum sum with the above criteria.

        List<Node> nodeList = new ArrayList<>();
        for(int i = 0; i < n; i++) nodeList.add(new Node());
        Graph a = getGraph(n), b = getGraph(n), c = getGraph(n);
        for(int [] edge : edges){ 
            a.addWeightedEdge(edge[0], edge[1], edge[2]);
            b.addWeightedEdge(edge[0], edge[1], edge[2]);
            c.addWeightedEdge(edge[1], edge[0], edge[2]);
        }
        a.dijkstra(src1);
        b.dijkstra(src2);
        c.dijkstra(dest);

       long minDistance = Long.MAX_VALUE/10;
       for(int i = 0; i < n; i++) {
           minDistance = Math.min(a.nodeList.get(i).dist + b.nodeList.get(i).dist + c.nodeList.get(i).dist, minDistance);
       }
       return minDistance == Long.MAX_VALUE/10? -1 : minDistance;
    }

    static Graph getGraph(int n) {
        List<Node> nodeList = new ArrayList<>();
        for(int i = 0; i < n; i++)
            nodeList.add(new Node());
        return new Graph(nodeList);
    }

    static class Graph{
        List<Node> nodeList;
        public Graph(List<Node> nodeList){this.nodeList = nodeList;}

        public void addWeightedEdge(int u, int v, int dist) {
            nodeList.get(u).neighbors.add(nodeList.get(v));
            long newDist = Math.min(nodeList.get(u).weightedEdges.getOrDefault(nodeList.get(v), (long)dist), dist);
            nodeList.get(u).weightedEdges.put(nodeList.get(v), newDist);
        }

        public void dijkstra(int src) {
            PriorityQueue<Node> pq = new PriorityQueue<>();
            pq.addAll(this.nodeList);
            this.nodeList.get(src).dist = 0;
            while(!pq.isEmpty()) {
                Node curNode = pq.poll();
                for(Node neighbor : curNode.neighbors) {
                        if(neighbor.dist > curNode.weightedEdges.get(neighbor) + curNode.dist){
                            neighbor.dist = curNode.dist + curNode.weightedEdges.get(neighbor);
                            pq.add(neighbor);
                        }
                }
            }

        }
    }
    static class Node implements Comparable<Node>{
        long dist;
        Map<Node, Long> weightedEdges;
        List<Node> neighbors;

        public Node(){
            this.weightedEdges = new HashMap<>();
            this.neighbors = new ArrayList<>();
            this.dist = Long.MAX_VALUE/10;
        }
        @Override
        public int compareTo(Node o){
            return (int)(this.dist - o.dist);
        }

    }
