			vector<long long> di(n+1,oo),s1i(n+1,oo),s2i(n+1,oo);
			dijkstra(src1,g,s1i);
			dijkstra(src2,g,s2i);
			dijkstra(dest,rg,di);
			long long res = oo;
			for(int i = 0 ; i < n ; i++){
				res = min(res,s1i[i] + s2i[i] + di[i]);
			}
			return res >= oo ?-1:res;
		}
	};

