/*
  Intuition: We look for intersections between paths from src1 and src2 to dest
             respectively and then get the shortest path from the intersection until
             dest. Then we compute the cost for the intersection =
             distFromSrc1 + distFromSrc2 + distToDest
             Then we select the intersection with the minimum cost, which is also the
             weight of the required minimum subgraph formed by src1->intersection,
             src2->intersection and intersection->dest
             NOTE: the destination is one such intersection in which case the graph is just
             src1->dest and src2->dest
*/

class Solution {
public:
  struct Edge {
    // The "from" and "to" of the edge
    int from, to;
    // The weight of the edge
    long long wt;
    Edge(int from, int to, long long wt) : from(from), to(to), wt(wt) {}
  };

  struct Node {
    // The outgoing edges from this node
    vector<Edge> outgoing;
    // The distance from src1 to this node, -1 if no path
    long long distFromSrc1 = -1;
    // The distance from src2 to this node, -1 if no path
    long long distFromSrc2 = -1;
    // The distance from this node to the destination, -1 if no path
    long long distToDest = -1;
  };

  // Traverses all paths from cur to dest and returns the length of the shortest one
  // and -1 if no such path
  // cur: The current node in the dfs
  long long distToDest(vector<Node>& graph, int cur, int src, long long dist,
                       int src1, int src2, int dest, vector<bool>& visited) {
    visited[cur] = true;
    auto& node = graph[cur];

    // Whether we are coming from src1
    if (src == src1) {
      if (node.distFromSrc1 == -1) {
        node.distFromSrc1 = dist;
      } else {
        node.distFromSrc1 = min(node.distFromSrc1, dist);
      }
    }

    // Whether we are coming from src2
    if (src == src2) {
      if (node.distFromSrc2 == -1) {
        node.distFromSrc2 = dist;
      } else {
        node.distFromSrc2 = min(node.distFromSrc2, dist);
      }
    }

    // We only need to compute the distance of this node to dest once
    if (node.distToDest != -1) {
      visited[cur] = false;
      return node.distToDest;
    }

    // If the current node is the destination
    if (cur == dest) {
      visited[cur] = false;
      node.distToDest = 0;
      return 0;
    }

    // Loop through all the outgoing edges to find a path to dest
    for (auto& edge: node.outgoing) {
      if (visited[edge.to]) {
        continue;
      }
      // Distance from "to" to dest
      long long nodeDistToDest = distToDest(graph, edge.to, src, dist + edge.wt,
                                             src1, src2, dest, visited);

      // No path from "to" to dest
      if (nodeDistToDest == -1) {
        continue;
      }
      long long distToDest = edge.wt + nodeDistToDest;
      if (node.distToDest == -1) {
        node.distToDest = distToDest;
      } else {
        node.distToDest = min(node.distToDest, distToDest);
      }
    }
    visited[cur] = false;
    return node.distToDest;
  }

  long long minimumWeight(int n, vector<vector<int>>& edges,int src1, int src2,
                          int dest) {
    // Build the graph
    vector<Node> graph(n + 1);
    for (auto& edge: edges) {
      int from = edge[0];
      int to = edge[1];
      long long wt = edge[2];
      graph[from].outgoing.push_back(Edge(from, to, wt));
    }

    // Records whether the node has been visited during DFS or not
    vector<bool> visited(n + 1, false);

    // Traverse from src1
    distToDest(graph, src1, src1, 0, src1, src2, dest, visited);
    // Traverse from src2
    distToDest(graph, src2, src2, 0, src1, src2, dest, visited);

    long long result = -1;
    for (int i = 0; i <= n; ++i) {
      auto& node = graph[i];

      // Check if this node is an intersection
      if (node.distFromSrc1 != -1 && node.distFromSrc2 != -1 &&
          node.distToDest != -1) {
        if (result == -1) {
          result = node.distFromSrc1 + node.distFromSrc2 + node.distToDest;
        } else {
          result = min(result, node.distFromSrc1 + node.distFromSrc2 +
                       node.distToDest);
        }
      }
    }
    return result;
  }
};
